var canvas;
var gfx;
var mouseDown = false;
var fill;

var mc = "#111111", cc = "#22ff22";

$(document).ready( function() {
  knittify();
} );

function knittify() {
  canvas = document.getElementById( "knittycanvas" );
  
  if(! canvas ) {
    //alert("oopsnocanvas")
    return;
  }
  $(canvas).ready( function() {
    canvas.width = patw*k;
    canvas.height = path*k;
    console.log("ready");
    for( var i = 0; i < pattern.length; i++ ) {
      if( pattern[i] == 0 )
        pattern[i] = 1;
      else if( pattern[i] == 1 )
        pattern[i] = 0;
      
    }
    if( canvas.getContext ) {
      ctx = canvas.getContext( '2d' );
      drawPattern();
      
    }
    $("#mcinput").val(mc);
    $("#mcinput").change( function() {
      //console.log( $(this).val() );
      mc = $(this).val()
      drawPattern();
    });
    $("#ccinput").val(cc);
    $("#ccinput").change( function() {
      //console.log( $(this).val() );
      cc = $(this).val()
      drawPattern();
    });
    $("#knittycanvas").mousemove( knittyMouseMove );
    $("#knittycanvas").mousedown( function(event) {
        mouseDown = true;
        var mx = event.pageX - $(canvas).offset().left;
        var my = event.pageY - $(canvas).offset().top;
        var x = Math.floor(mx*patw/canvas.width);
        var y = Math.floor(my*path/canvas.height);
        fill = pattern[x+y*patw];
        knittyMouseMove(event);
    } );
    $(document).mouseup( function() {
      mouseDown = false;
    } );
    
  });
}
function knittyMouseMove(event) {
  if(!mouseDown ) return;
  var mx = event.pageX - $(canvas).offset().left, my = event.pageY - $(canvas).offset().top;
  var x = Math.floor(mx*patw/canvas.width);
  var y = Math.floor(my*path/canvas.height);
  //console.log( "x: "+x+", y: "+y+" event: "+event+" cw: "+canvas.width );
  if( fill == 0 ) {
    ctx.fillStyle = mc;
    pattern[x+y*patw] = 1;
  } else if( fill == 1 ) {
    ctx.fillStyle = cc;
    pattern[x+y*patw] = 0;
  } else {
      ctx.fillStyle = "#ff0000"
  }
  ctx.fillRect( x*canvas.width/patw, y*canvas.height/path,
      canvas.width/patw, canvas.height/path );
  drawLines();
}
function drawPattern() {
  for( var x = 0; x < patw; x++ ) {
    for( var y = 0; y < path; y++ ) {
      if( pattern[x+y*patw] == 0 ) {
        ctx.fillStyle = mc;
        pattern[x+y*patw] = 1;
      } else if( pattern[x+y*patw] == 1 ) {
        ctx.fillStyle = cc;
        pattern[x+y*patw] = 0;
      } else {
        ctx.fillStyle = "#ff0000"
      }
      ctx.fillRect( x*canvas.width/patw, y*canvas.height/path, canvas.width/patw, canvas.height/path );
    }
  }
  drawLines();
}
function drawLines() {
  ctx.strokeStyle = "#ffffff";
  ctx.fillStyle = "#ffffff";
  ctx.textAlign = "center";
  ctx.textBaseline = "middle";
  
  ctx.beginPath();
  for( var x = k; x < canvas.width; x += k ) {
    
    ctx.moveTo(x, 0);
    ctx.lineTo(x,canvas.height);
  }
  for( var i = 1; i < patw + 1; i++ ) {
      ctx.strokeText(i, (i-0.5)*k, k/2);
  }
  for( var i = 2; i < path + 1; i++ ) {
      ctx.strokeText(i, k/2, (i-0.5)*k);
  }
  
  for( var y = k; y < canvas.height; y += k ) {
    ctx.moveTo(0,y);
    ctx.lineTo(canvas.width,y);
  }
  ctx.stroke();
}
